import React, { useState, useEffect } from 'react';
import { format, startOfMonth, endOfMonth, eachDayOfInterval, isSameMonth, isToday, addMonths, subMonths } from 'date-fns';
import { ChevronLeft, ChevronRight, Calendar as CalendarIcon, BarChart3 } from 'lucide-react';
import { diaryAPI } from '../services/api';

interface DayData {
  date: string;
  total_calories: number;
  total_protein: number;
  calories_goal: number;
  protein_goal: number;
  calories_met: boolean;
  protein_met: boolean;
  meal_count: number;
  has_data: boolean;
}

interface MonthData {
  year: number;
  month: number;
  days: DayData[];
}

const Diary: React.FC = () => {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [monthData, setMonthData] = useState<MonthData | null>(null);
  const [loading, setLoading] = useState(false);
  const [selectedDate, setSelectedDate] = useState<string | null>(null);
  const [selectedDayData, setSelectedDayData] = useState<DayData | null>(null);

  useEffect(() => {
    loadMonthData();
  }, [currentDate]);

  const loadMonthData = async () => {
    setLoading(true);
    try {
      const year = currentDate.getFullYear();
      const month = currentDate.getMonth() + 1;
      const response = await diaryAPI.getMonth(year, month);
      setMonthData(response.data);
    } catch (error) {
      console.error('Failed to load month data:', error);
    } finally {
      setLoading(false);
    }
  };

  const navigateMonth = (direction: 'prev' | 'next') => {
    setCurrentDate(prev => direction === 'next' ? addMonths(prev, 1) : subMonths(prev, 1));
  };

  const getDayStatus = (day: DayData) => {
    if (!day.has_data) return 'no-data';
    if (day.calories_met && day.protein_met) return 'both-met';
    if (day.calories_met || day.protein_met) return 'partial';
    return 'none-met';
  };

  const getDayColor = (day: DayData) => {
    const status = getDayStatus(day);
    switch (status) {
      case 'both-met':
        return 'bg-success-500 text-white';
      case 'partial':
        return 'bg-warning-500 text-white';
      case 'none-met':
        return 'bg-danger-500 text-white';
      default:
        return 'bg-gray-100 text-gray-400';
    }
  };

  const handleDateClick = async (date: string) => {
    setSelectedDate(date);
    try {
      const response = await diaryAPI.getDate(date);
      setSelectedDayData(response.data);
    } catch (error) {
      console.error('Failed to load date data:', error);
    }
  };

  const renderCalendar = () => {
    if (!monthData) return null;

    const start = startOfMonth(currentDate);
    const end = endOfMonth(currentDate);
    const days = eachDayOfInterval({ start, end });

    // Get the first day of the month to determine padding
    const firstDayOfMonth = start.getDay();
    const paddingDays = Array.from({ length: firstDayOfMonth }, (_, i) => i);

    return (
      <div className="grid grid-cols-7 gap-1">
        {/* Day headers */}
        {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
          <div key={day} className="p-2 text-center text-sm font-medium text-gray-500">
            {day}
          </div>
        ))}
        
        {/* Padding days */}
        {paddingDays.map((_, i) => (
          <div key={`padding-${i}`} className="p-2" />
        ))}
        
        {/* Calendar days */}
        {days.map(day => {
          const dateStr = format(day, 'yyyy-MM-dd');
          const dayData = monthData.days.find(d => d.date === dateStr);
          const isCurrentDay = isToday(day);
          
          return (
            <button
              key={day.toString()}
              onClick={() => handleDateClick(dateStr)}
              className={`
                p-2 h-16 text-sm font-medium rounded-lg transition-all duration-200
                ${getDayColor(dayData || { has_data: false } as DayData)}
                ${isCurrentDay ? 'ring-2 ring-primary-500 ring-offset-2' : ''}
                hover:opacity-80 focus:outline-none focus:ring-2 focus:ring-primary-500
              `}
            >
              <div className="text-center">
                <div className="font-semibold">{format(day, 'd')}</div>
                {dayData?.has_data && (
                  <div className="text-xs opacity-75">
                    {dayData.meal_count} meal{dayData.meal_count !== 1 ? 's' : ''}
                  </div>
                )}
              </div>
            </button>
          );
        })}
      </div>
    );
  };

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold text-gray-900">Nutrition Diary</h2>
        <div className="flex items-center space-x-2">
          <CalendarIcon className="h-5 w-5 text-gray-400" />
          <span className="text-sm text-gray-500">Track your daily nutrition goals</span>
        </div>
      </div>

      {/* Month Navigation */}
      <div className="flex items-center justify-between">
        <button
          onClick={() => navigateMonth('prev')}
          className="btn-secondary flex items-center space-x-2"
        >
          <ChevronLeft className="h-4 w-4" />
          <span>Previous</span>
        </button>
        
        <h3 className="text-xl font-semibold text-gray-900">
          {format(currentDate, 'MMMM yyyy')}
        </h3>
        
        <button
          onClick={() => navigateMonth('next')}
          className="btn-secondary flex items-center space-x-2"
        >
          <span>Next</span>
          <ChevronRight className="h-4 w-4" />
        </button>
      </div>

      {/* Legend */}
      <div className="flex flex-wrap items-center gap-4 text-sm">
        <div className="flex items-center space-x-2">
          <div className="w-4 h-4 bg-success-500 rounded"></div>
          <span>Both goals met</span>
        </div>
        <div className="flex items-center space-x-2">
          <div className="w-4 h-4 bg-warning-500 rounded"></div>
          <span>One goal met</span>
        </div>
        <div className="flex items-center space-x-2">
          <div className="w-4 h-4 bg-danger-500 rounded"></div>
          <span>No goals met</span>
        </div>
        <div className="flex items-center space-x-2">
          <div className="w-4 h-4 bg-gray-100 rounded"></div>
          <span>No data</span>
        </div>
      </div>

      {/* Calendar */}
      <div className="card">
        {loading ? (
          <div className="flex items-center justify-center h-64">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary-600"></div>
          </div>
        ) : (
          renderCalendar()
        )}
      </div>

      {/* Selected Date Details */}
      {selectedDate && selectedDayData && (
        <div className="card bg-gray-50">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            {format(new Date(selectedDate), 'EEEE, MMMM d, yyyy')}
          </h3>
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <h4 className="font-medium text-gray-700 mb-2">Calories</h4>
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Consumed:</span>
                  <span className="font-medium">{selectedDayData.total_calories}</span>
                </div>
                <div className="flex justify-between">
                  <span>Goal:</span>
                  <span className="font-medium">{selectedDayData.calories_goal}</span>
                </div>
                <div className="flex justify-between">
                  <span>Status:</span>
                  <span className={`font-medium ${
                    selectedDayData.calories_met ? 'text-success-600' : 'text-danger-600'
                  }`}>
                    {selectedDayData.calories_met ? 'Met ✓' : 'Not Met ✗'}
                  </span>
                </div>
              </div>
            </div>
            
            <div>
              <h4 className="font-medium text-gray-700 mb-2">Protein</h4>
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Consumed:</span>
                  <span className="font-medium">{selectedDayData.total_protein}g</span>
                </div>
                <div className="flex justify-between">
                  <span>Goal:</span>
                  <span className="font-medium">{selectedDayData.protein_goal}g</span>
                </div>
                <div className="flex justify-between">
                  <span>Status:</span>
                  <span className={`font-medium ${
                    selectedDayData.protein_met ? 'text-success-600' : 'text-danger-600'
                  }`}>
                    {selectedDayData.protein_met ? 'Met ✓' : 'Not Met ✗'}
                  </span>
                </div>
              </div>
            </div>
          </div>
          
          {selectedDayData.meal_count > 0 && (
            <div className="mt-4 pt-4 border-t border-gray-200">
              <p className="text-sm text-gray-600">
                <span className="font-medium">{selectedDayData.meal_count}</span> meal{selectedDayData.meal_count !== 1 ? 's' : ''} logged
              </p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Diary;
