                const startPadding = (monthStart.getDay() + 6) % 7; // Convert Sunday=0 to Monday=0
                const paddedDays = Array(startPadding).fill(null).concat(calendarDays);

                // Fill to 35 days (5 weeks)
                while (paddedDays.length < 35) {
